mapper.select:
SELECT

FROM drs_element a

WHERE a.id = #{id}

SELECT , d.label AS "dataTypeName" FROM drs_element a
LEFT JOIN (SELECT `value`,label FROM sys_dict WHERE type='data_type' AND del_flag='0')d ON a.data_type=d.`value` WHERE a.del_flag =#{page.params.delFlag}
AND a.name_cn LIKE CONCAT('%',#{element.nameCn},'%')
AND a.company_id = #{element.companyId}

        SELECT

        FROM drs_element a

       WHERE  a.del_flag = 0

				AND a.company_id = #{element.companyId}

        ORDER BY a.id

			LIMIT ${page.startRow}, ${page.endRow}

mapper.update:
	UPDATE drs_element set to_pool = #{toPool} 

	WHERE id IN 

        #{id}

	UPDATE drs_element set to_pool = 0 

		WHERE id IN 

       	 #{id}

mapper.delete:
		 DELETE FROM drs_element WHERE del_flag=0 

		AND id IN

            #{id}
