mapper.select:
SELECT
COUNT(1)
FROM
drs_information a
WHERE 
	a.del_flag =#{info.delFlag}

		AND a.company_id =#{info.companyId}


		AND a.info_type1 =#{info.infoType1}


		AND a.info_type2 =#{info.infoType2}

SELECT

FROM drs_information a
WHERE
	a.del_flag =#{info.delFlag}

		AND a.info_type1 =#{info.infoType1 }


		AND a.info_type2 =#{info.infoType2}

ORDER BY a.id

SELECT distinct
FROM drs_information a LEFT JOIN sys_company c ON c.id = a.company_id LEFT JOIN drs_info_element ie ON ie.info_id =a.id LEFT JOIN drs_element_pool el ON el.ele_id=ie.element_id WHERE el.id IN
#{id}

mapper.update:
UPDATE drs_information SET is_audit = 2 WHERE del_flag = 0 AND is_audit=1
AND depart_id IN
#{id}

